mapper.select:
select con.id, u.name as owner, con.source, con.customerId, con.fullname, con.appellation, con.email, con.mphone, con.job, con.birth, con.createBy, con.createTime, con.editBy, con.editTime, con.description, con.contactSummary, con.nextContactTime, con.address from tbl_contacts con join t_user u on u.id=con.owner
and con.fullname like '%' #{fullname} '%'
limit #{pageNo},#{pageSize}
